function addSuffixToFilesInFolder() {
  // ID da pasta no Google Drive (o que vem depois de "folders/" na URL da pasta)
  var folderId = '11j3QQlduOgJwaY2YA2PJRRKUavCHZnM-';
  var folder = DriveApp.getFolderById(folderId);
  var files = folder.getFiles();

  while (files.hasNext()) {
    var file = files.next();
    var name = file.getName();
    var ext = "";

    // Se o arquivo tiver uma extensão, separe-a do nome
    if (name.lastIndexOf('.') > 0) {
      ext = name.substring(name.lastIndexOf('.'));
      name = name.substring(0, name.lastIndexOf('.'));
    }

    // Novo nome do arquivo com o sufixo "Mês"
    var newName = 'Mês - ' + name  + ext;

    // Renomear o arquivo
    file.setName(newName);
  }

  Logger.log("Renomeação completa!");
}
