// Remoear arquivo baseado na data de 
function renomearArquivosPorData() {
  const PASTAS_ORIGEM_IDS = [
  "Id-pasta",
  "Id-pasta",
  "Id-pasta",
  "Id-pasta",
// Adicione mais IDs de pasta conforme necessário
  ];

  //  Data de referência (mutável para futuras modificações)
  const DATA_REFERENCIA = new Date(); // Padrão: hoje
  DATA_REFERENCIA.setHours(0, 0, 0, 0);

  //  Padrão de renomeação (mutável para futuras modificações)
  const PREFIXO_NOVO_NOME = "BUSCA ATIVA PERÍODO 30.01 À 07.02";

  let arquivosRenomeados = 0;

  PASTAS_ORIGEM_IDS.forEach((idPasta) => {
    const pasta = DriveApp.getFolderById(idPasta);
    const arquivos = pasta.getFiles();

    while (arquivos.hasNext()) {
      const arquivo = arquivos.next();
      const nomeAtual = arquivo.getName();
      const dataCriacao = arquivo.getDateCreated();

      // Verifica se a data do arquivo corresponde à data de referência
      if (dataCriacao.toDateString() === DATA_REFERENCIA.toDateString()) {
        try {
          const novoNome = `${PREFIXO_NOVO_NOME}`;
          arquivo.setName(novoNome);
          arquivosRenomeados++;
          Logger.log(` Renomeado: ${nomeAtual} → ${novoNome}`);
        } catch (e) {
          Logger.log(` Erro ao renomear ${nomeAtual}: ${e.message}`);
        }
      }
    }
  });

  Logger.log(`Total de arquivos renomeados: ${arquivosRenomeados}`);
}
